mapper.select:

select *
from(
    select rownum as rnum,SearchNoticeResult.*
    from ( 
    	select * 
		from notice
		where nno is not null

		order by regDate desc
    )  SearchNoticeResult

    where rownum <= #{endRow}		    
)		 
where rnum > #{startRow}

mapper.update:
update notice
set viewcnt=viewcnt+1
where nno=#{nno}

update notice 
set 
title=#{title},content=#{content}		
where nno=#{nno}
