mapper.select:
select *
from(
    select rownum as rnum,SearchNoticeResult.*
    from (
    	select *
		from notice
		where notice_number is not null
		
		
    )  SearchNoticeResult
    
    where rownum <= #{endRow}
)
where rnum > #{startRow}

mapper.update:
update notice
set notice_count = notice_count + 1
where notice_number = #{notice_number}

update notice
set 
notice_title = #{notice_title}, notice_content = #{notice_content}
where notice_number = #{notice_number}
